//calling Form ,showdeatils and title components

import React ,{Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import Titles from './Titles';
import Form from './Form';
import ShowDetails from './ShowDetails';


export default class TicketDetails extends Component{

  state = {
    train: undefined,
    route : undefined,
    ntickets: undefined,
    ticketPrice : undefined,
    id: undefined,
    time : undefined,
    error : undefined,
 

  }

  getTrain = async(e) =>{
  // to prevent the default action associated with the event 
  e.preventDefault();

    const id = e.target.elements.id.value;
    const ntickets = e.target.elements.ntickets.value;
   
    console.log(id);
    console.log(ntickets);

  //  Retriew the train tickets details from the backend server
   const api_calls =  await fetch(`http://localhost:4000/trainresevation/${id}`);
   const data = await api_calls.json();
   if( ntickets && id){
     
  // console.log(data);

   this.setState({
    route : data.route,
    ntickets: ntickets,
    ticketPrice: data.ticketPrice,
    id : data._id,
    time: data.time,
    error : ""
   });

 }else{
  this.setState({
    route : undefined,
    ntickets: undefined,
    ticketPrice: undefined,
    id : undefined,
    time: undefined,
    error: "Please enter  the value"
 
  });
 }
}

  render(){
      return(
            <div>
               <div className = "mainContainer">
                  <div className = "bodymain">
                      <div className = "container">
                        <div className = "row">
                          <div className = "col-xs-7 formcontainer">
                            <Form getTrain = {this.getTrain} />
                            <ShowDetails
                  
                              ntickets= {this.state.ntickets}
                              ticketPrice = {this.state.ticketPrice}
                              id = {this.state.id}
                              time = {this.state.time}
                              error = {this.state.error}
                 
                                 />
                   
                   
                          </div>
                          <div className = "col-xs-5 header-container">
                            <Titles />
                          </div>
                        </div>
                      </div>
                  </div>
      
                </div>
            </div>

      )
  }
}
